import React, { Component } from 'react';
import './App.css';
import './semantic.css';
import './style.css';
import 'onsenui/css/onsenui.css';
import 'onsenui/css/onsen-css-components.css';
import Sources from './Sources/Sources';
import MultiColorButton from './Buttons/MultiColorButton';
import Slider from './Slider/Slider.js';
import { Range, Fab, Icon } from 'react-onsenui';
class App extends Component {
  state = {
      white: {color:"white", required:0, present:0, enabled:true},
      blue: {color:"blue", required:0, present:0, enabled:true},
      black: {color:"black", required:0, present:0, enabled:true},
      red: {color:"red", required:0, present:0, enabled:true},
      green: {color:"green", required:0, present:0, enabled:true},
    }



    render() {
      return (
        <div className="App">
              <Fab>
               <Icon icon='fa-twitter' size={26} fixedWidth={false} style={{verticalAlign: 'middle'}} />
             </Fab>

          <hr/>
          <Sources
          hasWhite={this.state.white.enabled}
          hasBlue={this.state.blue.enabled}
          hasBlack={this.state.black.enabled}
          hasRed={this.state.red.enabled}
          hasGreen={this.state.green.enabled}
          />
          <br/><br/>
          <Range modifier="material"
            value={this.state.whiteTurn}
            onChange={(event) => this.setState({whiteTurn: parseInt(event.target.value)})}
            /><br/>
          <Range modifier="material"
            value={this.state.blueTurn}
            onChange={(event) => this.setState({blueTurn: parseInt(event.target.value)})}
            /><br/>
          <Range modifier="material"
            value={this.state.blackTurn}
            onChange={(event) => this.setState({blackTurn: parseInt(event.target.value)})}
            /><br/>
          <Range modifier="material"
            value={this.state.redTurn}
            onChange={(event) => this.setState({redTurn: parseInt(event.target.value)})}
            /><br/>
          <Range modifier="material"
            value={this.state.greenTurn}
            onChange={(event) => this.setState({greenTurn: parseInt(event.target.value)})}
            /><br/>
          {this.state.white.enabled && this.state.blue.enabled ?
            <MultiColorButton />
            :null
          }

        </div>
      );
    }
}

export default App;

