import React, { Component } from 'react';
import './App.css';
import './semantic.css';
import './style.css';
import SourceCounter from './SourceCounter/SourceCounter';
class App extends Component {
  state = {
      cards: [
        {title: "Mana Drain", cmc:2},
        {title: "Foil", cmc:4},
        {title: "Disallow", cmc: 3}
      ],
      showCards: false
    }

    render() {

      return (
        <div className="App">
          <SourceCounter classes="ui button white"/>
          <SourceCounter classes="ui button blue"/>
          <SourceCounter classes="ui button black"/>
          <SourceCounter classes="ui button red"/>
          <SourceCounter classes="ui button green"/>
        </div>
      );
    }
}

export default App;

