import React, { Component } from 'react';
import SourceCounter from '../SourceCounter/SourceCounter';


class Sources extends Component {
  state = {
      white: {color:"white", required:0, present:0},
      blue: {color:"blue", required:0, present:0},
      black: {color:"black", required:0, present:0},
      red: {color:"red", required:0, present:0},
      green: {color:"green", required:0, present:0}
    }

    addSource = () => {
      this.setState({
        sourcesRequired: [
          this.state.sourcesRequired[0]+1,
          this.state.sourcesRequired[1]+1,
          this.state.sourcesRequired[2]+1,
          this.state.sourcesRequired[3]+1,
          this.state.sourcesRequired[4]+1
        ]
      })
    }

    render() {
      return (
        <div className="ui container main">
          <SourceCounter classes="ui button white" required={this.state.sourcesRequired[0]}/>
          <SourceCounter classes="ui button blue" required={this.state.sourcesRequired[1]}/>
          <SourceCounter classes="ui button black" required={this.state.sourcesRequired[2]}/>
          <SourceCounter classes="ui button red" required={this.state.sourcesRequired[3]}/>
          <SourceCounter classes="ui button green" required={this.state.sourcesRequired[4]}/>
          <hr/>
          <button onClick={this.addSource}>Add global</button>
        </div>
      );
    }
}

export default Sources;
